mapper.select:
select cost.id, cost.cost, cost.create_time, cost.user_id, cost.detail, type.cost_type from cost cost left join cost_type type on cost.cost_type = type.id where user_id=#{userId}
and cost_type = #{costType}
and DATE_FORMAT( cost.create_time, '%Y%m' ) = DATE_FORMAT(#{createTime} ,'%Y%m')
and DATE_FORMAT( cost.create_time, '%Y%m' ) = DATE_FORMAT(CURDATE(),'%Y%m')
